"use strict";
var React = require("react")
var ReactRouter = require("react-router");
var Link = ReactRouter.Link;
var LogoSrc = require("./image/logo.png");
//var LogoSrcUrl = "data:image/png;base64,";
// Logo组件
var Logo = React.createClass({
    render: function () {
        // 链接样式
        var aStyle = {
            height: this.props.size ? this.props.size : "40px",
            display: "inline-block",
            textDecoration: "none"
        };
        // 图片样式
        var imgStyle = {
            width: this.props.size ? this.props.size : "40px",
            height: this.props.size ? this.props.size : "40px",
            marginRight: "10px",
            verticalAlign: "top"
        };
        // 文本样式
        var textStyle = {
            lineHeight: this.props.size ? this.props.size : "40px",
            fontSize: "14px",
            color: "#d8eaf4"
        };
        let projectName = "";
        if (!(typeof window === "undefined")) {
            projectName = Oplate.projectName == "ketao" ? "客套" : "OPLATE";
        }
        return (
            <Link to="/" className="logo" style={aStyle}>
                <img style={imgStyle} src={LogoSrc}/>
                <span style={textStyle}>{projectName}</span>
            </Link>
        );
    }
});

module.exports = Logo;
